mapper.select:
SELECT type_name 
FROM type_list 
WHERE word_type = #{word_type}

SELECT singer_id, singer_name 
FROM singer 
WHERE group_id = #{group_id}

SELECT * 
FROM search_data 
WHERE singer_id = #{singer_id} AND search_date = #{search_date}

mapper.update:
UPDATE search_data 
SET singer_count = singer_count + 1 
WHERE singer_id = #{singer_id} AND search_date = #{search_date}
